mapper.insert:
		INSERT	INTO	MEMBER 	(
									EMAIL
									, NAME
									, PASSWORD
									, SALT
									, LOGIN_FAIL_COUNT
									, LAST_LOGIN
                                    , ADMIN_YN
									, APPROVAL_STATUS	
									, PHONE
								 )
		VALUES					 (
									#{email}
									, #{name}
									, #{password}
									, #{salt}
									, 0
									, SYSDATE
                                    , 'N'
									, 'N'
									, #{phone}
								 )

mapper.select:

SELECT	SALT
FROM	MEMBER
WHERE	EMAIL = #{email}

SELECT	COUNT(*)
FROM	MEMBER
WHERE	EMAIL=#{email}

mapper.update:
UPDATE	MEMBER 
SET		LOGIN_FAIL_COUNT = 0
WHERE	EMAIL = #{email}

UPDATE	MEMBER
SET		APPROVAL_STATUS = 'Y'
WHERE	EMAIL = #{email}

UPDATE	MEMBER
SET		NAME = #{name}

		, PASSWORD = #{password}
		, SALT = #{salt}

		, PHONE = #{phone}
WHERE	EMAIL = #{email}

mapper.select:
SELECT	COUNT(1)
FROM	MEMBER
WHERE	EMAIL = #{email}
AND		NAME = #{name}
AND		PHONE = #{phone}

mapper.update:
UPDATE	MEMBER
SET		PASSWORD = #{password}
		, SALT = #{salt}
WHERE	EMAIL = #{email}
